package com.qhc.order.entity;import java.io.Serializable;import java.util.Date;import com.fasterxml.jackson.annotation.JsonFormat;/** *  * Function: Data transfer object. <br>  * * @author walker */public class BillingPlan		implements Serializable {	private static final long serialVersionUID = 1670639166697280070L;		/* Id */	private Integer id = null;	/* 订单详情ID */	private Integer orderInfoId = null;	/* 回款条款code */	private String code = null;	/* 回款起始日期 */	@JsonFormat(shape = JsonFormat.Shape.STRING, pattern="yyyy-MM-dd",timezone = "GMT+8")	private Date payDate = null;	/* 回款比例 */	private Float percentage = null;	/* 预算回款原币金额 */	private Double amount = null;	/* 预算回款金额 */	private Double rmbAmount = null;	/* 备注 */	private String reason = null;	public BillingPlan(){	}	public Integer getId() {		return this.id;	}	public void setId(Integer id) {		this.id = id;	}	 	public Integer getOrderInfoId() {		return this.orderInfoId;	}	public void setOrderInfoId(Integer orderInfoId) {		this.orderInfoId = orderInfoId;	}	 	public String getCode() {		return this.code;	}	public void setCode(String code) {		this.code = code;	}	 	public Date getPayDate() {		return this.payDate;	}	public void setPayDate(Date payDate) {		this.payDate = payDate;	}	 	public Float getPercentage() {		return this.percentage;	}	public void setPercentage(Float percentage) {		this.percentage = percentage;	}	 	public Double getAmount() {		return this.amount;	}	public void setAmount(Double amount) {		this.amount = amount;	}	 	public Double getRmbAmount() {		return this.rmbAmount;	}	public void setRmbAmount(Double rmbAmount) {		this.rmbAmount = rmbAmount;	}	 	public String getReason() {		return this.reason;	}	public void setReason(String reason) {		this.reason = reason;	}	 	@Override	public int hashCode() {		final int prime = 31;		int result = 1;		result = prime * result + ((id == null) ? 0 : id.hashCode());		return result;	}	@Override	public boolean equals(Object obj) {		if (this == obj) {			return true;		}		if (obj == null) {			return false;		}		if (getClass() != obj.getClass()) {			return false;		}		final BillingPlan other = (BillingPlan) obj;		return (this.id == null ? other.id == null : this.id.equals(other.id));	}		public String toString() {	    final String tab = "  ";	    String str = "";	    str = "BillingPlan ( "	        + "id = " + this.id + tab	        + "orderInfoId = " + this.orderInfoId + tab	        + "code = " + this.code + tab	        + "payDate = " + this.payDate + tab	        + "percentage = " + this.percentage + tab	        + "amount = " + this.amount + tab	        + "rmbAmount = " + this.rmbAmount + tab	        + "reason = " + this.reason + tab	        + " )";		    return str;	}}