package com.qhc.order.entity;import java.io.Serializable;import java.util.Date;/** *  * Function: Data transfer object. <br>  * * @author walker */public class Characteristics		implements Serializable {	private static final long serialVersionUID = -9038837127589180335L;		/* Id */	private Integer id = null;	/* ItemId */	private Integer itemId = null;	/* 选定的特征代码 */	private String keyCode = null;	/* 选定的特征值的代码 */	private String valueCode = null;	/* 可配置 */	private Integer isConfigurable = null;	public Characteristics(){	}	public Integer getId() {		return this.id;	}	public void setId(Integer id) {		this.id = id;	}	 	public Integer getItemId() {		return this.itemId;	}	public void setItemId(Integer itemId) {		this.itemId = itemId;	}	 	public String getKeyCode() {		return this.keyCode;	}	public void setKeyCode(String keyCode) {		this.keyCode = keyCode;	}	 	public String getValueCode() {		return this.valueCode;	}	public void setValueCode(String valueCode) {		this.valueCode = valueCode;	}	 	public Integer getIsConfigurable() {		return this.isConfigurable;	}	public void setIsConfigurable(Integer isConfigurable) {		this.isConfigurable = isConfigurable;	}	 	@Override	public int hashCode() {		final int prime = 31;		int result = 1;		result = prime * result + ((id == null) ? 0 : id.hashCode());		return result;	}	@Override	public boolean equals(Object obj) {		if (this == obj) {			return true;		}		if (obj == null) {			return false;		}		if (getClass() != obj.getClass()) {			return false;		}		final Characteristics other = (Characteristics) obj;		return (this.id == null ? other.id == null : this.id.equals(other.id));	}		public String toString() {	    final String tab = "  ";	    String str = "";	    str = "Characteristics ( "	        + "id = " + this.id + tab	        + "itemId = " + this.itemId + tab	        + "keyCode = " + this.keyCode + tab	        + "valueCode = " + this.valueCode + tab	        + "isConfigurable = " + this.isConfigurable + tab	        + " )";		    return str;	}}