package com.qhc.order.entity;import java.io.Serializable;import java.util.Date;/** *  * Function: Data transfer object. <br>  * * @author walker */public class ItemColor		implements Serializable {	private static final long serialVersionUID = 2512425635234705162L;		/* Id */	private Integer id = null;	/* 行项目ID */	private Integer itemId = null;	/* 喷粉分组 */	private String paintingClass = null;	/* 颜色编码 */	private String colorCode = null;	public ItemColor(){	}	public Integer getId() {		return this.id;	}	public void setId(Integer id) {		this.id = id;	}	 	public Integer getItemId() {		return this.itemId;	}	public void setItemId(Integer itemId) {		this.itemId = itemId;	}	 	public String getPaintingClass() {		return this.paintingClass;	}	public void setPaintingClass(String paintingClass) {		this.paintingClass = paintingClass;	}	 	public String getColorCode() {		return this.colorCode;	}	public void setColorCode(String colorCode) {		this.colorCode = colorCode;	}	 	@Override	public int hashCode() {		final int prime = 31;		int result = 1;		result = prime * result + ((id == null) ? 0 : id.hashCode());		return result;	}	@Override	public boolean equals(Object obj) {		if (this == obj) {			return true;		}		if (obj == null) {			return false;		}		if (getClass() != obj.getClass()) {			return false;		}		final ItemColor other = (ItemColor) obj;		return (this.id == null ? other.id == null : this.id.equals(other.id));	}		public String toString() {	    final String tab = "  ";	    String str = "";	    str = "ItemColor ( "	        + "id = " + this.id + tab	        + "itemId = " + this.itemId + tab	        + "paintingClass = " + this.paintingClass + tab	        + "colorCode = " + this.colorCode + tab	        + " )";		    return str;	}}