mapper.select:
select o.sequence_number as sequenceNumber, '' as contactor, so.name as officeName, sg.name as groupName, '' as contractDate, oi.contract_number as contractNumber, o.customer_code as customerCode, sc.name as customerName, scc.name as customerClassName, oi.shop_name as shopName, '' as terminalType, oi.is_special as isSpecial, oi.is_reformed as isReformed, oi.contract_rmb_value as contractRmbAmount, '' as groassProfit, oi.is_longterm as isLongterm, oi.discount as discount, da.address as address, oi.transfer_type as transferType, oi.earliest_delivery_date as shippDate, oi.install_type as installType, oi.receive_type as receiveType, oi.contactor1_id as contactorId, oi.contactor1_tel as contactorTel, '' as receiverId, oi.payment_type as paymentType, oi.freight as freight, i.period as period, oi.currency as currency, oi.contract_value as contractAmount, oi.currency_exchange as exchange, oi.is_new as isNew from k_order_info oi inner join k_order o on o.id = oi.order_id left join sap_customer sc on sc.code = o.customer_code left join sap_customer_class scc on scc.code = sc.sap_customer_class_code left join sap_industry_code sic on sic.code = oi.terminal_type left join k_contract c on c.order_info_id = oi.id left join sap_sales_office so on so.code = oi.office_code left join sap_sales_group sg on sg.code = oi.group_code
oi.is_active = 1
AND o.sequence_number like '%${sequenceNumber}%'
AND oi.contract_number like '%${contractNumber}%'
AND sc.name like '%${customerName}%'
AND DATE_FORMAT(oi.create_time, '%Y-%m-%d') >= #{createStartTime} AND DATE_FORMAT(oi.create_time, '%Y-%m-%d') <= #{createEndTime}
AND scc.code = #{customerClass}
AND oi.is_special = #{isSpecial}
AND oi.status = #{status}

select o.sequence_number as sequenceNumber, oi.contract_number as contractNumber, sc.name as customerName, oi.shop_name as shopName, u.name as salesName, oi.is_reformed as isReformed, oi.is_new as isNew, oi.is_convenient_store as isConvenientStore, si.name as industryName, sit.name as customerIndustryCodeName, oi.sale_type as saleType, oi.contract_Rmb_Value as contractRmbValue, oi.create_time as createTime, so.name as officeName, sg.name as groupName, oi.gross_profit_margin, o.quote_status as quoteStatus, oi.status from k_order_info oi inner join k_order o on o.id = oi.order_id left join sap_customer sc on sc.code = o.customer_code left join sap_industry_code sit on sit.code = sc.sap_industry_code_code left join sap_industry_and_customer sic on sic.sap_customer_code = o.customer_code left join sap_industry si on si.code = si.code = sic.sap_industry_code left join k_contract c on c.order_info_id = oi.id left join sap_sales_office so on so.code = oi.office_code left join sap_sales_group sg on sg.code = oi.group_code LEFT JOIN b_user u on u.user_identity=oi.creater
oi.is_active = 1 and o.st_order_type = 3 and oi.status = '05'
AND UPPER(o.sequence_number) like '%${sequenceNumber.toUpperCase()}%'
AND UPPER(oi.contract_number) like '%${contractNumber.toUpperCase()}%'
AND (UPPER(sc.code) like '%${customerName.toUpperCase()}%' or UPPER(sc.name) like '%${customerName.toUpperCase()}%')
AND UPPER(u.name) like '%${salesName.toUpperCase()}%'
AND sic.sap_industry_code = #{industry, jdbcType=VARCHAR}
AND sc.sap_industry_code_code = #{industryCode, jdbcType=VARCHAR}
AND oi.sale_type = #{saleType, jdbcType=VARCHAR}
AND oi.contract_rmb_value >= ${contractAmount1}
AND oi.contract_rmb_value <= ${contractAmount2}
AND DATE_FORMAT(oi.create_time, '%Y-%m-%d') >= '${createTime.substring(0, createTime.indexOf(" - "))}' and DATE_FORMAT(oi.create_time, '%Y-%m-%d') <= '${createTime.substring(createTime.indexOf(" - ") + 3)}'
AND oi.office_code = #{officeCode, jdbcType=VARCHAR}
AND o.quote_status = '01'
AND o.quote_status = '05'
AND oi.status = #{status, jdbcType=CHAR}

select o.sequence_number as sequenceNumber, '' as contactor, so.name as officeName, sg.name as groupName, '' as contractDate, oi.contract_number as contractNumber, o.customer_code as customerCode, sc.name as customerName, scc.name as customerClassName, oi.shop_name as shopName, sic.name as terminalType, oi.is_special as isSpecial, oi.is_reformed as isReformed, oi.contract_rmb_value as contractRmbAmount, '' as groassProfit, oi.is_longterm as isLongterm, oi.discount as discount, sm.description as materialName, i.material_code as materialCode, sm.is_purchased as isPurchased, i.quantity as quantity, i.actural_price as salesPrice, i.actural_price * i.quantity as salesAmount, sm.sap_unit_of_measurement_code as unitCode, da.address as address, oi.transfer_type as transferType, oi.earliest_delivery_date as shippDate, oi.install_type as installType, oi.receive_type as receiveType, oi.contactor1_id as contactorId, oi.contactor1_tel as contactorTel, '' as receiverId, oi.payment_type as paymentType, oi.freight as freight, i.period as period, '' as financialReviewer1, '' as financialReviewer2, oi.currency as currency, oi.contract_value as contractAmount, oi.currency_exchange as exchange, oi.is_new as isNew from k_order_info oi inner join k_order o on o.id = oi.order_id left join sap_customer sc on sc.code = o.customer_code left join sap_customer_class scc on scc.code = sc.sap_customer_class_code
left join k_contract c on c.order_info_id = oi.id left join k_item i on i.order_info_id = oi.id left join k_delivery_address da on da.order_info_id = oi.id and da.seq = i.delivery_address_seq left join sap_materials sm on sm.code = i.material_code left join sap_sales_office so on so.code = oi.office_code left join sap_sales_group sg on sg.code = oi.group_code
oi.is_active = 1
AND o.sequence_number like '%${sequenceNumber}%'
AND oi.contract_number like '%${contractNumber}%'
AND sc.name like '%${customerName}%'
AND DATE_FORMAT(oi.create_time, '%Y-%m-%d') >= #{createStartTime} AND DATE_FORMAT(oi.create_time, '%Y-%m-%d') <= #{createEndTime}
AND scc.code = #{customerClass}
AND oi.is_special = #{isSpecial}
AND oi.status = #{status}
