package com.qhc.sap.entity;import java.io.Serializable;import java.util.Date;/** *  * Function: Data transfer object. <br>  * * @author walker */public class ColorClass		implements Serializable {	private static final long serialVersionUID = 2126022033502323479L;		/* 颜色分组 */	private String colorClass = null;	/* 颜色可选项 */	private String colorCode = null;	/* 粉末物料号码 */	private String colorMaterialCode = null;	/* 颜色特性值描述 */	private String colorDescription = null;	public ColorClass(){	}	public String getColorClass() {		return this.colorClass;	}	public void setColorClass(String colorClass) {		this.colorClass = colorClass;	}	 	public String getColorCode() {		return this.colorCode;	}	public void setColorCode(String colorCode) {		this.colorCode = colorCode;	}	 	public String getColorMaterialCode() {		return this.colorMaterialCode;	}	public void setColorMaterialCode(String colorMaterialCode) {		this.colorMaterialCode = colorMaterialCode;	}	 	public String getColorDescription() {		return this.colorDescription;	}	public void setColorDescription(String colorDescription) {		this.colorDescription = colorDescription;	}	 	@Override	public int hashCode() {		final int prime = 31;		int result = 1;		result = prime * result + ((colorClass == null) ? 0 : colorClass.hashCode());		result = prime * result + ((colorCode == null) ? 0 : colorCode.hashCode());		return result;	}	@Override	public boolean equals(Object obj) {		if (this == obj) {			return true;		}		if (obj == null) {			return false;		}		if (getClass() != obj.getClass()) {			return false;		}		final ColorClass other = (ColorClass) obj;		return (this.colorClass == null ? other.colorClass == null : this.colorClass.equals(other.colorClass))			&&(this.colorCode == null ? other.colorCode == null : this.colorCode.equals(other.colorCode));	}		public String toString() {	    final String tab = "  ";	    String str = "";	    str = "ColorClass ( "	        + "colorClass = " + this.colorClass + tab	        + "colorCode = " + this.colorCode + tab	        + "colorMaterialCode = " + this.colorMaterialCode + tab	        + "colorDescription = " + this.colorDescription + tab	        + " )";		    return str;	}}