package com.qhc.sap.entity;import java.io.Serializable;import java.util.Date;/** *  * Function: Data transfer object. <br>  * * @author walker */public class MaterialProductClass		implements Serializable {	private static final long serialVersionUID = 652470796826650475L;		/* 物料编码 */	private String materialCode = null;	/* 产品系列号 */	private String productClass = null;	public MaterialProductClass(){	}	public String getMaterialCode() {		return this.materialCode;	}	public void setMaterialCode(String materialCode) {		this.materialCode = materialCode;	}	 	public String getProductClass() {		return this.productClass;	}	public void setProductClass(String productClass) {		this.productClass = productClass;	}	@Override	public String toString() {		return "MaterialProductClass [materialCode=" + materialCode + ", productClass=" + productClass + "]";	}}