package com.qhc.sap.entity;import java.io.Serializable;import java.util.Date;/** *  * Function: Data transfer object. <br>  * * @author walker */public class PaintingClass		implements Serializable {	private static final long serialVersionUID = -3705944935988576746L;		/* 喷粉部位编码 */	private String paintingClass = null;	/* 喷粉部位描述 */	private String paintingParts = null;	public PaintingClass(){	}	public String getPaintingClass() {		return this.paintingClass;	}	public void setPaintingClass(String paintingClass) {		this.paintingClass = paintingClass;	}	 	public String getPaintingParts() {		return this.paintingParts;	}	public void setPaintingParts(String paintingParts) {		this.paintingParts = paintingParts;	}	 	@Override	public int hashCode() {		final int prime = 31;		int result = 1;		result = prime * result + ((paintingClass == null) ? 0 : paintingClass.hashCode());		return result;	}	@Override	public boolean equals(Object obj) {		if (this == obj) {			return true;		}		if (obj == null) {			return false;		}		if (getClass() != obj.getClass()) {			return false;		}		final PaintingClass other = (PaintingClass) obj;		return (this.paintingClass == null ? other.paintingClass == null : this.paintingClass.equals(other.paintingClass));	}		public String toString() {	    final String tab = "  ";	    String str = "";	    str = "PaintingClass ( "	        + "paintingClass = " + this.paintingClass + tab	        + "paintingParts = " + this.paintingParts + tab	        + " )";		    return str;	}}