package com.qhc.sap.entity;import java.io.Serializable;import java.util.Date;/** *  * Function: Data transfer object. <br>  * * @author walker */public class ProductClass		implements Serializable {	private static final long serialVersionUID = -5289033685604855764L;		/* 产品系列号 */	private String productClass = null;	/* 喷粉部位 */	private String paintingClass = null;	/* 颜色分组 */	private String colorClass = null;	/* 默认颜色选项 */	private String defaultColor = null;	/* 喷粉部位描述 */	private String paintingParts = null;	public ProductClass(){	}	public String getProductClass() {		return this.productClass;	}	public void setProductClass(String productClass) {		this.productClass = productClass;	}	 	public String getPaintingClass() {		return this.paintingClass;	}	public void setPaintingClass(String paintingClass) {		this.paintingClass = paintingClass;	}	 	public String getColorClass() {		return this.colorClass;	}	public void setColorClass(String colorClass) {		this.colorClass = colorClass;	}	 	public String getDefaultColor() {		return this.defaultColor;	}	public void setDefaultColor(String defaultColor) {		this.defaultColor = defaultColor;	}	public String getPaintingParts() {		return paintingParts;	}	public void setPaintingParts(String paintingParts) {		this.paintingParts = paintingParts;	}	@Override	public String toString() {		return "ProductClass [productClass=" + productClass + ", paintingClass=" + paintingClass + ", colorClass="				+ colorClass + ", defaultColor=" + defaultColor + ", paintingParts=" + paintingParts + "]";	}}