package com.qhc.sap.entity;import java.io.Serializable;import java.util.Date;/** *  * Function: Data transfer object. <br>  * * @author walker */public class Tax		implements Serializable {	private static final long serialVersionUID = -2176238426880145503L;		/* Code */	private String code = null;	/* Tax */	private Double tax = null;	/* Remark */	private String remark = null;	public Tax(){	}	public String getCode() {		return this.code;	}	public void setCode(String code) {		this.code = code;	}	 	public Double getTax() {		return this.tax;	}	public void setTax(Double tax) {		this.tax = tax;	}	 	public String getRemark() {		return this.remark;	}	public void setRemark(String remark) {		this.remark = remark;	}	 	@Override	public int hashCode() {		final int prime = 31;		int result = 1;		result = prime * result + ((code == null) ? 0 : code.hashCode());		return result;	}	@Override	public boolean equals(Object obj) {		if (this == obj) {			return true;		}		if (obj == null) {			return false;		}		if (getClass() != obj.getClass()) {			return false;		}		final Tax other = (Tax) obj;		return (this.code == null ? other.code == null : this.code.equals(other.code));	}		public String toString() {	    final String tab = "  ";	    String str = "";	    str = "Tax ( "	        + "code = " + this.code + tab	        + "tax = " + this.tax + tab	        + "remark = " + this.remark + tab	        + " )";		    return str;	}}