mapper.select:
SELECT m.code, m.description, m.is_configurable, m.is_purchased, m.stand_price as standard_price, m.opt_time, m.material_size, m.sap_unit_of_measurement_code as unit_code, u.name as unit_name, m.sap_material_groups_code as group_code, mg.name as group_name, m.sap_clazz_code as clazz_code, c.name as clazz_name, mgo.code as st_group_code, mgo.name as st_group_name FROM sap_materials m LEFT JOIN sap_clazz c ON c.code = m.sap_clazz_code LEFT JOIN sap_unit_of_measurement u ON u.code = m.sap_unit_of_measurement_code LEFT JOIN sap_material_groups mg ON mg.code = m.sap_material_groups_code LEFT JOIN b_material_group_order mgo ON mgo.code = mg.b_material_group_order_code
AND m.code = #{code, jdbcType=VARCHAR}
AND ( UPPER(m.description) like '%${name}%' or UPPER(m.code) like '%${name}%' )

SELECT c.code, c.change_date, c.name, c.address, c.sap_customer_class_code as clazz_code, cc.name as clazz_name, c.sap_industry_code_code as industry_code_code, ic.name as industry_code_name, i.code as industry_code, i.name as industry_name FROM sap_customer c LEFT JOIN sap_industry_code ic ON ic.code = c.sap_industry_code_code LEFT JOIN sap_customer_class cc on cc.code = c.sap_customer_class_code LEFT JOIN sap_industry_and_customer iac on iac.sap_customer_code = c.code LEFT JOIN sap_industry i on i.code = iac.sap_industry_code
AND c.sap_customer_class_code = #{clazzCode, jdbcType=VARCHAR}
AND c.name like '%${name, jdbcType=VARCHAR}%'
